(()=>{try{const e=document.documentElement,t=localStorage.getItem("theme"),o="light"===t||"dark"===t?t:window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light";e.dataset.theme=o,e.classList.add("preload-theme")}catch{}window.addEventListener("DOMContentLoaded",()=>{requestAnimationFrame(()=>document.documentElement.classList.remove("preload-theme"))})})();
